(function(device, dom, sizes){

	/* --- Mobile --- */
	device.support = Modernizr;

	/* --- Mobile --- */
	device.isMobile = device.support.touch;
	dom.$html.addClass(device.isMobile ? 'd-mobile' : 'd-no-mobile');

	/* --- Phone --- */
	var phoneCheck = function(){
		device.isPhone = (sizes.width<768);
		dom.$html.addClass(device.isPhone ? 'd-phone' : 'd-no-phone');
		dom.$html.removeClass(device.isPhone ? 'd-no-phone' : 'd-phone');
	};
	app.dom.$window.on('resize.phone-check', phoneCheck);
	phoneCheck();

	/* --- Retina --- */
	device.isRetina = (window.devicePixelRatio && window.devicePixelRatio>1);

	/* --- iOS --- */
	if (navigator.userAgent.match(/iPad/i)) {
		dom.$html.addClass('d-ipad');
		device.isIPad = true;
	};
	if (navigator.userAgent.match(/(iPhone|iPod touch)/i)) {
		dom.$html.addClass('d-iphone');
		device.isIPhone = true;
	};
	if (navigator.userAgent.match(/(iPad|iPhone|iPod touch)/i)) {
		dom.$html.addClass('d-ios');
		device.isIOS = true;
	};
	if (navigator.userAgent.match(/.*CPU.*OS 7_\d/i)) {
		dom.$html.addClass('d-ios7');
		device.isIOS7 = true;
	};

	/* --- iPad (for fix wrong window height) --- */
	if (dom.$html.hasClass('d-ipad d-ios7')) {
		dom.$window.on('resize orientationchange focusout', function(){
			window.scrollTo(0,0);
		});
	};

	if(device.isMobile) {
		dom.$window.on('hover',function(event){
			event.preventDefault();
		});
	}

})(app.device, app.dom, app.sizes);

(function(sizes, viewport){

	// {fn} get window sizes
	var getSizes = function(){
		return {
			width: app.dom.$window.width(),
			height: parseInt(window.innerHeight,10)
		}
	};

	// {fn} get window viewport (for media-queries)
	var getViewport = function(){
		var element = window,
		    prefix = 'inner';
		if (!(prefix+'Width' in window)) {
			prefix = 'client';
			element = document.documentElement || document.body;
		}
		return {
			width: element[prefix+'Width'],
			height: element[prefix+'Height']
		};
	};

	// {fn} update sizes
	var update = function(){
		app.sizes = getSizes();
		app.viewport = getViewport();
	};

	// {event} window resize
	app.dom.$window.on('resize.app', update);

	// get current values
	update();

})(app.sizes, app.viewport);
(function(utils, config){

	utils.delayRender = function(callback){
		return setTimeout(callback, config.renderDelay);
	};

	utils.delayDuration = function(callback){
		return setTimeout(callback, config.duration);
	};

	utils.prevent = function(e){
		e.preventDefault();
	};

})(app.utils, app.config);
$.fn.folioSlider = function() {
  $(this).each(function(){
    var selector = $(this);
    var folioSlider = Class.extend({
      init: function($block) {

        this.$block = $block;
        this.$loader = $block.find('.folio-slider__loader');
        this.$nav = $block.find('.folio-slider__nav');
        this.$navInner = $block.find('.folio-slider__nav__inner');

        this.navItemTemplate = _.template($('#slider-nav-template').html(), { variable:'item' });
        var slider = this;
        this.sly = new Sly(slider.$nav,{
          horizontal: 1,
          itemNav: 'basic',
          smart: 1,
          activateOn: 'click',
          scrollBy: 1,
          mouseDragging: 1,
          touchDragging: 1,
          swingSpeed: 0.2,
          scrollBar: $('.scrollbar'),
          dragHandle: 1,
          speed: 600,
          startAt: 0,
          activeClass: 'folio-slider__nav__item_active'
        }).init();
        this.worksUrl = $block.data('url');
        this.works = [];
        this.currentSlide = null;

        this.bindActions();
        this.showLoader();
        this.getWorks();

      },
      showLoader: function() {
        this.$loader.addClass('folio-slider__loader_visible');
      },
      hideLoader: function() {
        this.$loader.removeClass('folio-slider__loader_visible');
      },
      showNavigation: function() {
        this.$nav.addClass('folio-slider__nav_visible');
      },
      hideNavigation: function() {
        this.$nav.removeClass('folio-slider__nav_visible');
      },
      renderNavigation: function() {
        var slider = this;
        _.each(slider.works,function(currWork){
          slider.$navInner.append(slider.navItemTemplate(currWork.getData()));
        });
      },
      renderSlides: function() {
        var slider = this;
        _.each(slider.works,function(currWork){
          currWork.render();
        });
      },
      getWorks: function() {
        var slider = this;
        $.ajax({
          url: slider.worksUrl,
          type: "POST",
          success: function(result) {
            // slider.hideLoader();
            _.each(result,function(resultData){
              var sliderItem = new SliderItem(resultData);
              slider.works.push(sliderItem);
            });
            slider.renderSlides();
            slider.renderNavigation();

            setTimeout(function(){
              slider.sly.reload();
              slider.sly.activate(0);
            },700);
          },
          error: function() {
            console.log("Can't load works");
          }
        })
      },
      getSlide: function(slideId) {
        var slider = this;
        var currentSlide = false;
        _.each(slider.works,function(work){
          if(work.data.id === slideId) {
            currentSlide = work;
          }
        });
        return currentSlide;
      },
      bindActions: function() {
        var slider = this;
        app.dom.$window.on('load',function(){
          setTimeout(function(){
            slider.showNavigation();
            slider.hideLoader();
            slider.sly.reload();
            app.dom.$root.find('[data-normalize]').imageNormalize();
          },1000);
        });
        app.dom.$window.on('resize',function(){
          slider.sly.reload();
          slider.$block.find('[data-normalize]').imageNormalize();
        });
        slider.sly.on('active',function(event,currentIndex){
          var currentItemId = $(slider.$nav.find('.folio-slider__nav__item')[currentIndex]).data('item-id');
          if(slider.currentSlide !== null) {
            slider.getSlide(slider.currentSlide).hide();
          }
          slider.getSlide(currentItemId).show();
          slider.currentSlide = currentItemId;
        });
      },
    });

    var SliderItem = Class.extend({
      init: function(data) {
        this.$container = selector.find('.folio-slider__content');
        this.$block = null;

        this.template = _.template($('#slider-item-template').html(), { variable:'item' });
        this.data = data;
      },
      render: function() {
        this.$block = $(this.template(this.data));
        this.$container.append(this.$block);
      },
      hide: function() {
        if(this.$block !== null)
          this.$block.removeClass('folio-slider__block_visible');
      },
      show: function() {
        if(this.$block !== null)
          this.$block.addClass('folio-slider__block_visible');
      },
      getData: function() {
        return this.data;
      }
    });

    new folioSlider($(this));
  });
}

/*
 * Image Normalizer
 * Stretches current image to parent's block size
 * and keeping image proportions
 */
$.fn.imageNormalize = function() {
  console.log('normalize');
  $(this).each(function(){
    var $imageBlock = $(this);
    var blockWidth = parseInt($imageBlock.width());
    var blockHeight = parseInt($imageBlock.height());
    var $image = $imageBlock.find('img');
    var iWidth = parseInt($image.width());
    var iHeight = parseInt($image.height());

    $image.css('width','auto');
    $image.css('height','auto');
      console.log('>');
      console.log(blockWidth/blockHeight);
      console.log(iWidth/iHeight);
      if(blockWidth/blockHeight > parseInt($image.width())/parseInt($image.height()))
        $image.css('width','100%');
      else
        $image.css('height','100%');
      var margin = -(parseInt($image.width()) - parseInt($imageBlock.width()))/2;
      $image.css('margin-left',margin + 'px');
      $image.css('margin-right',margin + 'px');
  });
}

$(function(){

	// app.dom.$root.find('.some-block').fadeOut(app.config.duration);
	var $root = app.dom.$root;

	$root.find('.folio-slider').folioSlider();

	// binding menu buttons
	$root.find('.header__menu-btn').on('click',function(){
		$root.find('.header__menu').addClass('header__menu_visible');
		setTimeout(function(){
			$root.find('.header__menu__wrapper').addClass('header__menu__wrapper_visible');
		},app.config.duration/2);
	});
	$root.find('.header__menu__close,.header__menu__background').on('click',function(){
		$root.find('.header__menu__wrapper').removeClass('header__menu__wrapper_visible');
		setTimeout(function(){
			$root.find('.header__menu').removeClass('header__menu_visible');
		},app.config.duration*2);
	});
	// normalize images on window resize
	app.dom.$window.on('resize',function(){
		$root.find('[data-normalize]').imageNormalize();
	});


});
